mapper.insert:

	select LAST_INSERT_ID()

insert into sys_logistics
( 

		addressee_name


		,addressee


		,addressee_phone


		,sender_name


		,sender_add


		,ems_status


		,retreat_status


		,retreat_info


		,create_date


		,update_date


		,create_by

) 
values (

		#{addresseeName}


		,#{addressee}


		,#{addresseePhone}


		,#{senderName}


		,#{senderAdd}


		,#{emsStatus}


		,#{retreatStatus}


		,#{retreatInfo}


		,#{createDate}


		,#{updateDate}


		,#{createBy}

)

mapper.select:
		SELECT 
		FROM sys_logistics
		WHERE  1=1 

				 and addressee_name=#{addresseeName}


				 and addressee=#{addressee}


				 and addressee_phone=#{addresseePhone}


				 and sender_name=#{senderName}


				 and sender_add=#{senderAdd}


				 and ems_status=#{emsStatus}


				 and retreat_status=#{retreatStatus}


				 and retreat_info=#{retreatInfo}


				 and create_date=#{createDate}


				 and update_date=#{updateDate}


				 and create_by=#{createBy}


                order by #{orderBy}
